// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–µ—Ä–µ–≤–æ–¥–∞ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –ø—Ä–æ–µ–∫—Ç–∞
export interface WorkTranslations {
  title: string;
  description: string;
  category: string;
  city?: string;
}

export function translateCategory(category: string, language: Language): string {
  if (!category) return category
  const translations = categoryTranslations[language]
  return translations?.[category] || category
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –ø–µ—Ä–µ–≤–µ–¥–µ–Ω–Ω—ã—Ö –∑–Ω–∞—á–µ–Ω–∏–π —Ä–∞–±–æ—Ç—ã –ø–æ—Ä—Ç—Ñ–æ–ª–∏–æ
export interface WorkTranslations {
  title: string;
  description: string;
  category: string;
  city?: string;
}

export function getTranslatedWork(
  work: { title: string; description: string; category: string; city?: string; translations?: Record<string, WorkTranslations> },
  language: Language
): { title: string; description: string; category: string; city?: string } {
  // –ï—Å–ª–∏ –µ—Å—Ç—å –ø–µ—Ä–µ–≤–æ–¥—ã –¥–ª—è —Ç–µ–∫—É—â–µ–≥–æ —è–∑—ã–∫–∞, –∏—Å–ø–æ–ª—å–∑—É–µ–º –∏—Ö
  if (work.translations && work.translations[language]) {
    const translation = work.translations[language]
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –ø–µ—Ä–µ–≤–æ–¥—ã –Ω–µ –ø—É—Å—Ç—ã–µ
    if (translation.title && translation.description) {
      return {
        title: translation.title,
        description: translation.description,
        category: translation.category ? translateCategory(translation.category, language) : translateCategory(work.category, language),
        city: translation.city || work.city
      }
    }
  }

  // –ï—Å–ª–∏ –ø–µ—Ä–µ–≤–æ–¥–æ–≤ –Ω–µ—Ç –∏–ª–∏ –æ–Ω–∏ –ø—É—Å—Ç—ã–µ, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –æ—Ä–∏–≥–∏–Ω–∞–ª
  // –í –±—É–¥—É—â–µ–º –∑–¥–µ—Å—å –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å fallback –Ω–∞ –ø–µ—Ä–µ–≤–æ–¥ –≤ —Ä–µ–∞–ª—å–Ω–æ–º –≤—Ä–µ–º–µ–Ω–∏
    return {
      title: work.title,
      description: work.description,
      category: translateCategory(work.category, language),
      city: work.city
  }
}

